import React, { useState, useEffect } from "react";
import "./App.css";
import MenuItem from "@material-ui/core/MenuItem";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InfoBox from "./InfoBox";
import Map from "./MapContainer";
import { Card, CardContent } from "@material-ui/core";
import Table from './Table';
import {sortData} from './util';
import LineGraph from './LineGraph';
import "leaflet/dist/leaflet.css";

const App = () => {
  const [countries, setcountries] = useState();
  const [country, setcountry] = useState("worldwide");
  const [countryInfo, setCountryInfo] = useState("");
  const [tableData, setTableData]=useState([]);
  const [mapCenter, setMapCenter] = useState({lat: 34.80746, lng:-40.4796});
  const [mapZoom, setMapZoom] = useState(3);

  useEffect(()=>{
    fetch('https://disease.sh/v3/covid-19/all')
    .then(response => response.json())
    .then(data =>{
      setCountryInfo(data)
    })
  }, []); 

  useEffect(() => {
    const getCountriesData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
        .then((response) => response.json())
        .then((data) => {
          const countries = data.map((country) => ({
            name: country.country,
            value: country.countryInfo.iso2, //IND, UK, USA
          }));

          const sortedData = sortData(data);
          setTableData(sortedData);
          setcountries(countries);
        });
    };
    getCountriesData();
  }, []);

  const onChangingCountry = async (event) => {
    const countryCode = event.target.value;
    setcountry(countryCode);

    const url= countryCode=='worldwide'? 'https://disease.sh/v3/covid-19/all' :`https://disease.sh/v3/covid-19/countries/${countryCode}`;

    //https://disease.sh/v3/covid-19/all    ----> World wide url
    // https://disease.sh/v3/covid-19/countries/[countryCode] ---->country wise

    await fetch(url).then(response => response.json())
    .then(data =>{
      setcountry(countryCode);
      setCountryInfo(data);
      console
      setMapCenter([data.countryInfo.lat,data.countryInfo.lng])
    })
  };

  // console.log(countryInfo);

  return (
    <div className="app">
      <div className="app__left">
        <div className="app__header">
          <h1>Covid-19 Tracker</h1>
          <FormControl className="app__dropdown">
            <Select
              variant="outlined"
              value={country}
              onChange={onChangingCountry}
            >
              <MenuItem value={"worldwide"}>Worldwide</MenuItem>
              {countries.map((country) => (
              <MenuItem value={country.value}>{country.name}</MenuItem>
            ))}

              {/* <MenuItem value={"worldwide"}>Worldwide</MenuItem>
            <MenuItem value={"worldwide"}>Option 1</MenuItem>
            <MenuItem value={"worldwide"}>Option 2</MenuItem>
            <MenuItem value={"worldwide"}>lets go yeah</MenuItem> */}
            </Select>
          </FormControl>
        </div>
        {/* Header */}
        {/* title+ select input dropdown field  */}

        <div className="app_stats">
          <InfoBox title="Covid Cases" cases={countryInfo.todayCases} total={countryInfo.cases} />
          <InfoBox title="Recovered" cases={countryInfo.todayRecovered} total={countryInfo.recovered} />
          <InfoBox title="Death" cases={countryInfo.todayDeaths} total={countryInfo.deaths} />
        </div>

        {/* infoboxs */}
        {/* infoboxs */}
        {/* infoboxs */}

        {/* map */}
        <Map 
        center={mapCenter}
        zoom={mapZoom}
        />
      </div>
      <div>
        <Card className="app__right">
        <CardContent>
          {/* table */}
          <h3>Live cases by Country</h3>
          <Table countries={tableData}/>
          <h3>Worldwide new Cases</h3>
          <LineGraph/>
          {/* graph */}
        </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default App;
